import React, { useEffect, useState } from "react";
import QRCode from "qrcode";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Carousel, CarouselItem, Image } from "react-bootstrap";

function Data() {
  const [token, setToken] = useState(() =>
    JSON.parse(localStorage.getItem("token"))
  );
  const [property, setProperty] = useState({});
  const [propertyHistory, setPropertyHistory] = useState([]);
  const [url, setImageURL] = useState("");

  if (!token) {
    window.location.pathname = "/signin";
  }

  let { address } = useParams();


  useEffect(async () => {
    console.log(address);
    const url1 = `https://35.209.170.73:4000/channels/mychannel/chaincodes/landRecord?args=["${address}"]&fcn=queryLand`;
    const url2 = `https://35.209.170.73:4000/channels/mychannel/chaincodes/landRecord?args=["${address}"]&fcn=getHistoryForAsset`;
    let conf = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "application/json",
      },
    };
    const response = await axios.get(url1, conf);
    setProperty(() => response.data.result);
    console.log(response.data.result);
    const response2 = await axios.get(url2, conf);
    setPropertyHistory(() => response2.data.result);

    const url = await QRCode.toDataURL(address);
    setImageURL(url);
  }, []);

  function copyText(id) {
    var text = document.getElementById(id).innerText;
    var elem = document.createElement("textarea");
    document.body.appendChild(elem);
    elem.value = text;
    elem.select();
    document.execCommand("copy");
    document.body.removeChild(elem);
  }

  return (
    <div className="container">
      <div className="row justify-content-center row-content">
        <div className="col-12">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <Carousel
                    interval={null}
                    id="carousel-example-generic"
                    data-ride="carousel"
                    indicators={false}
                    className="d-none d-lg-block"
                  >
                    <CarouselItem className="carousel-item-imgs">
                      <Image
                        className="d-block mh-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                  <Carousel
                    interval={null}
                    id="carousel-example-generic2"
                    data-ride="carousel"
                    indicators={false}
                    className="d-none d-md-block d-lg-none"
                  >
                    <CarouselItem className="carousel-item-imgs3">
                      <Image
                        className="d-block mh-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                  <Carousel
                    interval={null}
                    id="carousel-example-generic3"
                    data-ride="carousel"
                    indicators={false}
                    className="d-flex justify-content-center align-items-center d-md-none bg-black"
                    style={{ height: "400px" }}
                  >
                    <CarouselItem className="carousel-item-imgs2 bg-black">
                      <Image
                        style={{
                          maxHeight: "400px",
                        }}
                        className="d-block mw-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                </div>
              </div>
            </div>
          </div>
          <div className="row my-2">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <h4>Details</h4>
                  <div className="row">
                    <div className="col-12 col-lg-6">
                      <div className="row">

                        <div className="col-6">
                          <p className="text-secondary">House Number</p>
                        </div>

                        <div className="col-6 d-flex">
                          <p className="h6" id="address">
                            {property.address}
                          </p>
                          <i
