import React, { useEffect, useState } from "react";
import QRCode from "qrcode.react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Carousel, CarouselItem, Image } from "react-bootstrap";

function Data() {
  const [token, setToken] = useState(() =>
    JSON.parse(localStorage.getItem("token"))
  );
  const [property, setProperty] = useState({});
  const [propertyHistory, setPropertyHistory] = useState([]);
  const [url, setImageURL] = useState("");

  // if (!token) {
  //   window.location.pathname = "/signin"
  // }

  let { address } = useParams();
  address = address.replaceAll("+", " ");

  // const url1 = `http://localhost:4000/channels/mychannel/chaincodes/fabcar?args=["${address}"]&fcn=queryCar`;
  // const url2 = `http://localhost:4000/channels/mychannel/chaincodes/fabcar?args=["${address}"]&fcn=getHistoryForAsset`;
  // let conf = {
  //   headers: {
  //     Authorization: `Bearer ${token}`,
  //     "Content-Type": "application/json",
  //   },
  // };
  // useEffect(() => {
  //   async function fetchData() {
  //     const response = await axios.get(url1, conf);
  //     setProperty(() => response.data.result);
  //     const response2 = await axios.get(url2, conf);
  //     setPropertyHistory(() => response2.data.result);
  //   }
  //   fetchData();
  // }, []);35.209.170.73

  const url1 = `http://35.209.170.73:4000/channels/mychannel/chaincodes/fabcar?args=["${address}"]&fcn=queryCar`;
  const url2 = `http://35.209.170.73:4000/channels/mychannel/chaincodes/fabcar?args=["${address}"]&fcn=getHistoryForAsset`;
  let conf = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
  };
  useEffect(async () => {
    const response = await axios.get(url1, conf);
    setProperty(() => response.data.result);
    const response2 = await axios.get(url2, conf);
    setPropertyHistory(() => response2.data.result);
    // const canvas = await QRCode.toCanvas(response.data.result.address, { errorCorrectionLevel: 'H' })
    // var container = document.getElementById('qrcode')
    // container.appendChild(canvas)
    const url = await QRCode.toDataURL(address);
    setImageURL(url);
  }, []);

  function copyText(id) {
    var text = document.getElementById(id).innerText;
    var elem = document.createElement("textarea");
    document.body.appendChild(elem);
    elem.value = text;
    elem.select();
    document.execCommand("copy");
    document.body.removeChild(elem);
  }

  return (
    <div className="container">
      <div className="row justify-content-center row-content">
        <div className="col-12">
          <div className="row">
            <div className="col-12">
              <div className="card">
                <div className="card-body">
                  <Carousel
                    interval={null}
                    id="carousel-example-generic"
                    data-ride="carousel"
                    indicators={false}
                    className="d-none d-lg-block"
                  >
                    <CarouselItem className="carousel-item-imgs">
                      <Image
                        className="d-block mh-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                  <Carousel
                    interval={null}
                    id="carousel-example-generic2"
                    data-ride="carousel"
                    indicators={false}
                    className="d-none d-md-block d-lg-none"
                  >
                    <CarouselItem className="carousel-item-imgs3">
                      <Image
                        className="d-block mh-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                  <Carousel
                    interval={null}
                    id="carousel-example-generic3"
                    data-ride="carousel"
                    indicators={false}
                    className="d-flex justify-content-center align-items-center d-md-none bg-black"
                    style={{ height: "400px" }}
                  >
                    <CarouselItem className="carousel-item-imgs2 bg-black">
                      <Image
                        style={{
                          maxHeight: "400px",
                        }}
                        className="d-block mw-100 mx-auto"
                        src={property.propertyImage}
                        alt=""
                      />
                    </CarouselItem>
                  </Carousel>
                </div>
              </div>
